import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { AuthConsumer } from '../helper/AuthContext'

function SignIn() {
    const navigate = useNavigate()
    const error = document.getElementsByClassName('error')
    const [form, setForm] = useState({ email: '', password: '' })

    const onSignIn = (event, login) => {
        event.preventDefault()
        const users = JSON.parse(localStorage.getItem('users'))

        if (validate(form)) {
            if (!users) {
                error[0].textContent = '*Please SignUp'
            } else if (isUserValid(users)) {
                login()
                navigate('/home')
            }
        }
    }

    // HELPER

    const validate = () => {
        error[1].textContent = form.email.match(
            /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/
        )
            ? ''
            : '*Invalid Email'

        return error[1].textContent === ''
    }

    const isUserValid = (users) => {
        let count = 0
        users.forEach((user) => {
            if (user.email === form.email) {
                if (user.password === form.password) {
                    error[0].textContent = ''
                    count++
                } else {
                    error[2].textContent = '*Wrong password'
                }
            }
        })

        if (count === 0) {
            error[0].textContent = '*New user, Please SignUp'
        } else {
            return true
        }
    }

    //HELPER END

    return (
        <section id="signin" className="center-my-child">
            <AuthConsumer>
                {({ login }) => (
                    <form
                        name="signupForm"
                        onSubmit={(event) => onSignIn(event, login)}
                    >
                        <header>SignIn</header>
                        <p className="error"></p>
                        <div className="input-field-wrapper">
                            <div className="input-field">
                                <label htmlFor="email">Email</label>
                                <br />
                                <input
                                    type="email"
                                    name="email"
                                    className="email"
                                    onChange={(e) =>
                                        setForm({
                                            ...form,
                                            email: e.target.value,
                                        })
                                    }
                                />
                                <p className="error"></p>
                            </div>
                            <div className="input-field">
                                <label htmlFor="password">Password</label>
                                <br />
                                <input
                                    type="password"
                                    name="password"
                                    className="password"
                                    onChange={(e) =>
                                        setForm({
                                            ...form,
                                            password: e.target.value,
                                        })
                                    }
                                />
                                <p className="error"></p>
                            </div>
                        </div>
                        <div className="btn-wrapper">
                            <button
                                type="submit"
                                id="signin-btn"
                                className="btn"
                            >
                                GO
                            </button>
                        </div>
                        <p className="or-link">
                            or <br />
                            <Link to="/">SignUp</Link>
                        </p>
                    </form>
                )}
            </AuthConsumer>
        </section>
    )
}

export default SignIn
