import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'

function SignUp() {
    let navigate = useNavigate()
    const error = document.getElementsByClassName('error')
    const [form, setForm] = useState({ email: '', password: '' })

    const onSignUp = (event) => {
        event.preventDefault()
        const data = {
            uuid: create_UUID(),
            email: form.email.value,
            password: form.password.value,
        }
        const users = JSON.parse(localStorage.getItem('users'))

        if (validate(form)) {
            if (!users) {
                localStorage.setItem('users', JSON.stringify([data]))
                navigate('/home')
            } else if (isUserSignedUp(users, data.email)) {
                localStorage.setItem('users', JSON.stringify([...users, data]))
                navigate('/home')
            } else {
                form.children[1].textContent = '*User Exist, SignIn'
            }
        }
    }

    // HELPERS

    const validate = (form) => {
        form.children[2].children[0].children[3].textContent =
            form.email.value.match(
                /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/
            )
                ? ''
                : '*Invalid Email'

        form.children[2].children[2].children[3].textContent =
            form.password.value === form.confirmPassword.value
                ? ''
                : '*Enter same password'

        return (
            form.children[2].children[0].children[3].textContent ===
            form.children[2].children[2].children[3].textContent
        )
    }

    const isUserSignedUp = (users, email) => {
        let count = 0
        users.forEach((user) => {
            if (user.email === email) {
                count++
            }
        })
        return count === 0
    }

    const create_UUID = () => {
        var dt = new Date().getTime()
        var uuid = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(
            /[xy]/g,
            function (c) {
                var r = (dt + Math.random() * 16) % 16 | 0
                dt = Math.floor(dt / 16)
                return (c === 'x' ? r : (r & 0x3) | 0x8).toString(16)
            }
        )
        return uuid
    }

    // HELPERS END

    return (
        <section id="signup" className="center-my-child">
            <form name="signinForm" onSubmit={onSignUp}>
                <header>SignUp</header>
                <p className="error"></p>
                <div className="input-field-wrapper">
                    <div className="input-field">
                        <label htmlFor="email">Email</label>
                        <br />
                        <input
                            type="email"
                            name="email"
                            className="email"
                            required
                        />
                        <p className="error"></p>
                    </div>
                    <div className="input-field">
                        <label htmlFor="password">Password</label>
                        <br />
                        <input
                            type="password"
                            name="password"
                            className="password"
                            required
                        />
                        <p className="error"></p>
                    </div>
                    <div className="input-field">
                        <label htmlFor="confirmPassword">
                            Confirm Password
                        </label>
                        <br />
                        <input
                            type="password"
                            name="confirmPassword"
                            className="password"
                            required
                        />
                        <p className="error"></p>
                    </div>
                </div>
                <div className="btn-wrapper">
                    <button type="submit" id="signup-btn" className="btn">
                        GO
                    </button>
                </div>
                <p className="or-link">
                    or <br />
                    <Link to="/signin">SignIn</Link>
                </p>
            </form>
        </section>
    )
}

export default SignUp
