import React from 'react'
import { Link, useNavigate } from 'react-router-dom'

function SignIn() {
    let navigate = useNavigate()

    const onSignIn = (event) => {
        event.preventDefault()
        const form = document.signupForm
        const data = {
            email: form.email.value,
            password: form.password.value,
        }
        const users = JSON.parse(localStorage.getItem('users'))

        if (validate(form)) {
            if (!users) {
                localStorage.setItem('users', JSON.stringify([data]))
                navigate('/')
            } else if (isUserSignedUp(users, data.email)) {
                localStorage.setItem('users', JSON.stringify([...users, data]))
                navigate('/home')
            } else {
                alert('You are already signed up, please signin')
                navigate('/signin')
            }
        }
    }

    // HELPER

    const validate = (form) => {
        form.children[1].children[0].children[3].textContent =
            form.email.value.match(
                /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/
            )
                ? ''
                : '*Invalid Email'

        form.children[1].children[1].children[3].textContent =
            form.password.value === form.confirmPassword.value
                ? ''
                : '*Wrong password'

        return (
            form.children[1].children[0].children[3].textContent ===
            form.children[1].children[2].children[3].textContent
        )
    }

    const isUserSignedUp = (users, email) => {
        let count = 0
        users.forEach((user) => {
            if (user.email === email) {
                count++
            }
        })
        return count === 0
    }

    //HELPER END
    return (
        <section id="signin" className="center-my-child">
            <form name="signupForm" onSubmit={onSignIn}>
                <header>SignIn</header>
                <div className="input-field-wrapper">
                    <div className="input-field">
                        <label htmlFor="email">Email</label>
                        <br />
                        <input
                            type="email"
                            name="email"
                            className="email"
                            required
                        />
                        <p className="error"></p>
                    </div>
                    <div className="input-field">
                        <label htmlFor="password">Password</label>
                        <br />
                        <input
                            type="password"
                            name="password"
                            className="password"
                            required
                        />
                        <p className="error"></p>
                    </div>
                </div>
                <div className="btn-wrapper">
                    <button type="submit" id="signin-btn" className="btn">
                        GO
                    </button>
                </div>
                <p className="or-link">
                    or <br />
                    <Link to="/">SignUp</Link>
                </p>
            </form>
        </section>
    )
}

export default SignIn
