import React, { useState } from 'react'

const AuthContext = React.createContext()

function AuthProvider({ children }) {
    const loggedInUserId = localStorage.getItem('activeUserId')

    const [isAuth, setIsAuth] = useState(loggedInUserId === '' ? false : true)
    const [activeUserId, setActiveUserId] = useState(loggedInUserId)

    const login = (loggedInUserId) => {
        setIsAuth(true)
        localStorage.setItem('activeUserId', loggedInUserId)
        setActiveUserId(loggedInUserId.uuid)
    }

    const logout = () => {
        setIsAuth(false)
        localStorage.setItem('activeUserId', '')
        setActiveUserId(null)
    }

    return (
        <AuthContext.Provider
            value={{
                isAuth: isAuth,
                activeUserId: loggedInUserId,
                login: login,
                logout: logout,
            }}
        >
            {children}
        </AuthContext.Provider>
    )
}

const AuthConsumer = AuthContext.Consumer

export { AuthProvider, AuthConsumer }
