//UUID
export const getUUID = () => {
    var dt = new Date().getTime()
    var uuid = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(
        /[xy]/g,
        function (c) {
            var r = (dt + Math.random() * 16) % 16 | 0
            dt = Math.floor(dt / 16)
            return (c === 'x' ? r : (r & 0x3) | 0x8).toString(16)
        }
    )
    return uuid
}

//VALIDATION
let errorMsg = ''

export const validateProfile = (file) => {
    errorMsg = file && file.type.includes('image') ? '' : 'Not an image'
    return errorMsg
}

export const validateEmail = (email) => {
    errorMsg = email.match(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/)
        ? ''
        : '*Invalid Email'
    return errorMsg
}

export const validatePhone = (phone) => {
    errorMsg = phone.match(/^\d{10}$/) ? '' : '*Invalid phone'
    return errorMsg
}

export const validatePassword = (password1, password2, message) => {
    console.log(!!password1)
    return errorMsg
}
