import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { AuthConsumer } from '../helper/AuthContext'

function SignUp() {
    let navigate = useNavigate()
    const error = document.getElementsByClassName('error')
    const [form, setForm] = useState({
        email: '',
        password: '',
        confirmPassword: '',
    })

    const onSignUp = (event, login) => {
        event.preventDefault()
        const users = JSON.parse(localStorage.getItem('users'))

        if (validate()) {
            if (!users) {
                localStorage.setItem('users', JSON.stringify([form]))
                navigate('/home')
            } else if (isUserSignedUp(users)) {
                localStorage.setItem('users', JSON.stringify([...users, form]))
                navigate('/home')
            } else {
                form.children[1].textContent = '*User Exist, SignIn'
            }
        }
    }

    // HELPERS

    const validate = () => {
        error[1] = form.email.match(
            /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/
        )
            ? ''
            : '*Invalid Email'

        error[3] =
            form.password === form.confirmPassword ? '' : '*Enter same password'

        return error[1] === error[3]
    }

    const isUserSignedUp = (users) => {
        let count = 0
        users.forEach((user) => {
            if (user.email === form.email) {
                count++
            }
        })
        return count === 0
    }

    const create_UUID = () => {
        var dt = new Date().getTime()
        var uuid = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(
            /[xy]/g,
            function (c) {
                var r = (dt + Math.random() * 16) % 16 | 0
                dt = Math.floor(dt / 16)
                return (c === 'x' ? r : (r & 0x3) | 0x8).toString(16)
            }
        )
        return uuid
    }

    // HELPERS END

    return (
        <section id="signup" className="center-my-child">
            <AuthConsumer>
                {({ login }) => (
                    <form
                        name="signinForm"
                        onSubmit={(event) => onSignUp(event, login)}
                    >
                        <header>SignUp</header>
                        <p className="error"></p>
                        <div className="input-field-wrapper">
                            <div className="input-field">
                                <label htmlFor="email">Email</label>
                                <br />
                                <input
                                    type="email"
                                    name="email"
                                    className="email"
                                    required
                                    onChange={(e) =>
                                        setForm({
                                            ...form,
                                            email: e.target.value,
                                        })
                                    }
                                />
                                <p className="error"></p>
                            </div>
                            <div className="input-field">
                                <label htmlFor="password">Password</label>
                                <br />
                                <input
                                    type="password"
                                    name="password"
                                    className="password"
                                    required
                                    onChange={(e) =>
                                        setForm({
                                            ...form,
                                            password: e.target.value,
                                        })
                                    }
                                />
                                <p className="error"></p>
                            </div>
                            <div className="input-field">
                                <label htmlFor="confirmPassword">
                                    Confirm Password
                                </label>
                                <br />
                                <input
                                    type="password"
                                    name="confirmPassword"
                                    className="password"
                                    required
                                    onChange={(e) =>
                                        setForm({
                                            ...form,
                                            confirmPassword: e.target.value,
                                        })
                                    }
                                />
                                <p className="error"></p>
                            </div>
                        </div>
                        <div className="btn-wrapper">
                            <button
                                type="submit"
                                id="signup-btn"
                                className="btn"
                            >
                                GO
                            </button>
                        </div>
                        <p className="or-link">
                            or <br />
                            <Link to="/signin">SignIn</Link>
                        </p>
                    </form>
                )}
            </AuthConsumer>
        </section>
    )
}

export default SignUp
