import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { AuthConsumer } from '../helper/AuthContext'

function SignIn() {
    let navigate = useNavigate()

    const [form, setForm] = useState({ email: '', password: '' })

    const onSignIn = (login) => {
        const users = JSON.parse(localStorage.getItem('users'))

        if (validate(form)) {
            if (!users) {
                form.children[1].textContent = '*Please SignUp'
            } else if (isUserValid(users)) {
                login()
                navigate('/home')
            }
        }
    }

    // HELPER

    const validate = (form) => {
        form.children[2].children[0].children[3].textContent =
            form.email.value.match(
                /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/
            )
                ? ''
                : '*Invalid Email'

        return form.children[2].children[0].children[3].textContent === ''
    }

    const isUserValid = (users, form, email, password) => {
        let count = 0
        users.forEach((user) => {
            if (user.email === email) {
                if (user.password === password) {
                    form.children[2].children[1].children[3].textContent = ''
                    count++
                } else {
                    form.children[2].children[1].children[3].textContent =
                        '*Wrong password'
                }
            }
        })

        if (count === 0) {
            form.children[1].textContent = '*New user, Please SignUp'
        } else {
            return true
        }
    }

    //HELPER END

    return (
        <section id="signin" className="center-my-child">
            <AuthConsumer>
                {({ login }) => (
                    <form name="signupForm" onSubmit={() => onSignIn(login)}>
                        <header>SignIn</header>
                        <p className="error"></p>
                        <div className="input-field-wrapper">
                            <div className="input-field">
                                <label htmlFor="email">Email</label>
                                <br />
                                <input
                                    type="email"
                                    name="email"
                                    className="email"
                                    required
                                    onChange={(e) =>
                                        setForm({ email: e.target.value })
                                    }
                                />
                                <p className="error"></p>
                            </div>
                            <div className="input-field">
                                <label htmlFor="password">Password</label>
                                <br />
                                <input
                                    type="password"
                                    name="password"
                                    className="password"
                                    required
                                    onChange={(e) =>
                                        setForm({ password: e.target.value })
                                    }
                                />
                                <p className="error"></p>
                            </div>
                        </div>
                        <div className="btn-wrapper">
                            <button
                                type="submit"
                                id="signin-btn"
                                className="btn"
                            >
                                GO
                            </button>
                        </div>
                        <p className="or-link">
                            or <br />
                            <Link to="/">SignUp</Link>
                        </p>
                    </form>
                )}
            </AuthConsumer>
        </section>
    )
}

export default SignIn
