import React, { useState } from 'react'

const AuthContext = React.createContext()

function AuthProvider({ children }) {
    const loggedInUserId = JSON.parse(localStorage.getItem('activeUserId'))

    const [isAuth, setIsAuth] = useState(!loggedInUserId ? false : true)
    const [activeUserId, setActiveUserId] = useState(
        !loggedInUserId ? undefined : loggedInUserId
    )

    const login = (loggedInUserId) => {
        setIsAuth(true)
        localStorage.setItem('activeUserId', loggedInUserId)
        setActiveUserId(loggedInUserId.uuid)
    }

    const logout = () => {
        setIsAuth(false)
        localStorage.setItem('activeUserId', undefined)
        setActiveUserId(undefined)
    }

    return (
        <AuthContext.Provider
            value={
                {
                    // isAuth: isAuth,
                    // activeUserId: activeUserId,
                    // login: login,
                    // logout: logout,
                }
            }
        >
            {children}
        </AuthContext.Provider>
    )
}

const AuthConsumer = AuthContext.Consumer

export { AuthProvider, AuthConsumer }
