import React, { useState } from 'react'
import { Button, Form, Modal } from 'react-bootstrap'
import {
    validateEmail,
    validatePhone,
    validateProfile,
} from '../helper/HelperFunctions'

function FormModal({ title, initialFormValues, updateContacts, contactIndex }) {
    const [show, setShow] = useState(false)
    const [form, setForm] = useState(initialFormValues)
    const [canSubmit, setCanSubmit] = useState(false)

    const error = document.getElementsByClassName('error')

    const handleClose = () => setShow(false)
    const handleShow = () => setShow(true)

    const imageUpload = (e) => {
        const file = e.target.files[0]

        error[0].textContent = validateProfile(file)

        getBase64(file).then((base64) => {
            setForm({ ...form, profile: base64 })
        })
    }
    const getBase64 = (file) => {
        return new Promise((resolve, reject) => {
            const reader = new FileReader()
            reader.onload = () => resolve(reader.result)
            reader.onerror = (error) => reject(error)
            reader.readAsDataURL(file)
        })
    }

    const onFormSubmit = (e) => {
        e.preventDefault()
        validate()
        if (canSubmit) {
            const userContacts = JSON.parse(
                localStorage.getItem('userContacts')
            )

            if (!userContacts) {
                localStorage.setItem('userContacts', JSON.stringify([form]))
            } else {
                localStorage.setItem(
                    'userContacts',
                    JSON.stringify([...userContacts, form])
                )
            }
            updateContacts()
            handleClose()
        }
    }

    const validate = () => {
        error[2] = validateEmail(form.email)
        error[3] = validatePhone(form.phone)

        setCanSubmit(
            error.some((ele) => {
                return ele.textContent !== ''
            })
        )
    }

    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Edit
            </Button>

            <Modal show={show} onHide={handleClose} centered>
                <Form onSubmit={onFormSubmit}>
                    <Modal.Header closeButton>
                        <Modal.Title>{title}</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form.Group
                            controlId="formFile"
                            className="mb-3"
                            onChange={(e) => {
                                imageUpload(e)
                            }}
                        >
                            <Form.Label>Profile</Form.Label>
                            <Form.Control type="file" />
                            <Form.Text className="error"></Form.Text>
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="formBasicName">
                            <Form.Label>Name</Form.Label>
                            <Form.Control
                                type="text"
                                placeholder="Enter Name"
                                onChange={(e) =>
                                    setForm({
                                        ...form,
                                        name: e.target.value,
                                    })
                                }
                                value={form.name}
                            />
                            <Form.Text className="error"></Form.Text>
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="formBasicEmail">
                            <Form.Label>Email address</Form.Label>
                            <Form.Control
                                type="email"
                                placeholder="Enter email"
                                onChange={(e) =>
                                    setForm({
                                        ...form,
                                        email: e.target.value,
                                    })
                                }
                                value={form.email}
                            />
                            <Form.Text className="error"></Form.Text>
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="formBasicPhone">
                            <Form.Label>Phone Number</Form.Label>
                            <Form.Control
                                type="tel"
                                placeholder="Enter phone number"
                                onChange={(e) =>
                                    setForm({
                                        ...form,
                                        phone: e.target.value,
                                    })
                                }
                                value={form.phone}
                            />
                            <Form.Text className="error"></Form.Text>
                        </Form.Group>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button type="submit" variant="primary">
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Form>
            </Modal>
        </>
    )
}

export default FormModal
