import React from 'react'
import { Button, Figure } from 'react-bootstrap'

function TableRow({ userContact }) {
    const userContactKeys = Object.keys(userContact)

    return (
        <tr>
            {userContactKeys.map((userContactKey, i) => {
                const key = 'td' + i //UNIQUE KEY
                if (
                    userContactKey !== 'userId' &&
                    userContactKey !== 'contactId'
                ) {
                    if (userContactKey === 'profile') {
                        return (
                            <td key={key}>
                                <Figure>
                                    <Figure.Image
                                        width={50}
                                        height={50}
                                        alt="user-profile"
                                        src={userContact[userContactKey]}
                                        roundedCircle
                                    />
                                </Figure>
                            </td>
                        )
                    } else {
                        if (i === userContactKeys.length - 1) {
                            return (
                                <>
                                    <td key={key}>
                                        {userContact[userContactKey]}
                                    </td>
                                    <td key={key + i}>
                                        <Button>Edit</Button>
                                    </td>
                                    <td key={key + i + 1}>
                                        <Button>Delete</Button>
                                    </td>
                                </>
                            )
                        } else {
                            return (
                                <td key={key}>{userContact[userContactKey]}</td>
                            )
                        }
                    }
                }
            })}
        </tr>
    )
}
export default TableRow
