import React from 'react'
import { Navigate } from 'react-router-dom'
import { AuthConsumer } from '../helper/AuthContext'

function DefineRoute({ children, isRouteAuthenticated = true }) {
    return (
        <AuthConsumer>
            {({ isAuth }) => {
                console.log(
                    'auth:',
                    isAuth,
                    '  isRoute:',
                    isRouteAuthenticated,
                    '  child:',
                    children.type.name
                )
                if (isAuth && isRouteAuthenticated) {
                    console.log('go to child')
                    return children
                } else if (isAuth && !isRouteAuthenticated) {
                    console.log('go to home')
                    return <Navigate to="/home" />
                } else if (!isAuth && isRouteAuthenticated) {
                    console.log('go to signin')
                    return <Navigate to="/signin" />
                } else if (!isAuth && !isRouteAuthenticated) {
                    console.log('go to child')
                    return children
                }
            }}
        </AuthConsumer>
    )
}

export default DefineRoute
