import React from 'react'
import { Button, Figure } from 'react-bootstrap'

function TableRow({ userContact }) {
    const userContactKeys = Object.keys(userContact)
    const userContactInfosVisible = []

    userContactKeys.forEach((userContactKey) => {
        if (userContactKey !== 'userId' && userContactKey !== 'contactId') {
            return userContactInfosVisible.push(userContactKey)
        }
    })

    return (
        <tr>
            {userContactKeys.map((userContactKey, i) => {
                const key = 'td' + i //UNIQUE KEY
                if (userContactKey === 'profile') {
                    return (
                        <td key={key}>
                            <Figure>
                                <Figure.Image
                                    width={50}
                                    height={50}
                                    alt="user-profile"
                                    src={userContact[userContactKey]}
                                    roundedCircle
                                />
                            </Figure>
                        </td>
                    )
                } else {
                    if (i === userContactKeys.length - 1) {
                        return (
                            <React.Fragment key={'rf' + i}>
                                <td key={key}>{userContact[userContactKey]}</td>
                                <td key={key + i}>
                                    <Button>Edit</Button>
                                </td>
                                <td key={key + i + 1}>
                                    <Button>Delete</Button>
                                </td>
                            </React.Fragment>
                        )
                    } else {
                        return <td key={key}>{userContact[userContactKey]}</td>
                    }
                }
            })}
        </tr>
    )
}
export default TableRow
