import React from 'react'
import { Button, Figure } from 'react-bootstrap'

function TableRow({ userContact }) {
    const userContactKeys = Object.keys(userContact)
    const userContactKeysToBeShown = []

    userContactKeys.forEach((userContactKey) => {
        if (userContactKey !== 'userId' && userContactKey !== 'contactId') {
            userContactKeysToBeShown.push(userContactKey)
        }
    })

    const handleEdit = () => {}
    const handleDelete = () => {
        console.log(userContact.contactId)
    }

    return (
        <tr>
            {userContactKeysToBeShown.map((userContactKeyToBeShown, i) => {
                const key = 'td' + i //UNIQUE KEY
                if (userContactKeyToBeShown === 'profile') {
                    return (
                        <td key={key}>
                            <Figure>
                                <Figure.Image
                                    width={50}
                                    height={50}
                                    alt="user-profile"
                                    src={userContact[userContactKeyToBeShown]}
                                    roundedCircle
                                />
                            </Figure>
                        </td>
                    )
                } else {
                    if (i === userContactKeysToBeShown.length - 1) {
                        return (
                            <React.Fragment key={'rf' + i}>
                                <td key={key}>
                                    {userContact[userContactKeyToBeShown]}
                                </td>
                                <td key={key + i}>
                                    <Button onClick={() => handleEdit()}>
                                        Edit
                                    </Button>
                                </td>
                                <td key={key + i + 1}>
                                    <Button onClick={() => handleDelete()}>
                                        Delete
                                    </Button>
                                </td>
                            </React.Fragment>
                        )
                    } else {
                        return (
                            <td key={key}>
                                {userContact[userContactKeyToBeShown]}
                            </td>
                        )
                    }
                }
            })}
        </tr>
    )
}
export default TableRow
