            <>
                <div className="logout-btn-wrapper">
                    <button
                        id="logout-btn"
                        className="btn"
                        onClick={() => logout()}
                    >
                        Logout
                    </button>
                </div>

                <main className="contact-list-wrapper">
                    <MyModal
                        uuid={activeUserId}
                        title={'Add Contact'}
                        type={'add'}
                    />
                    <header>Contact List</header>
                    <table id="contact-table">
                        <thead>
                            <tr className="table-row" id="table-header-row">
                                <th className="table-row-item">Profile</th>
                                <th className="table-row-item">Name</th>
                                <th className="table-row-item">Email</th>
                                <th className="table-row-item">Phone number</th>
                            </tr>
                        </thead>
                        {/* <tbody>
                        <tr className="table-row table-data-row">
                            <td className="table-row-item">Img</td>
                            <td className="table-row-item">Vatsal Patel</td>
                            <td className="table-row-item">vatsal@email.com</td>
                            <td className="table-row-item">1111111111</td>
                        </tr>
                    </tbody> */}
                        {getUserContact(activeUserId)}
                    </table>
                </main>
            </>
            {/* )}
