import React, { useState } from 'react'
import { AuthConsumer } from '../helper/AuthContext'
import MyModal from '../components/MyModal'
import { Alert, Button, Table } from 'react-bootstrap'

function Home() {
    const [userContact, setUserContact] = useState(undefined)

    const getUserContact = (userId) => {
        const users = JSON.parse(localStorage.getItem('users'))
        users.forEach((user) => {
            console.log(user.uuid, userId)
            if (user.uuid === userId) {
                setUserContact({
                    profile: user.contact.profile,
                    name: user.contact.name,
                    email: user.contact.email,
                    phone: user.contact.number,
                })
            }
        })
    }

    return (
        <section id="home">
            <AuthConsumer>
                {({ activeUserId, logout }) => (
                    <>
                        {/* <div className="logout-btn-wrapper"> */}
                        <Button variant="danger" onClick={() => logout()}>
                            Logout
                        </Button>
                        {/* </div> */}

                        <main className="contact-list-wrapper">
                            <MyModal
                                uuid={activeUserId}
                                title={'Add Contact'}
                                type={'add'}
                            />

                            <Alert.Heading className="mb-3">
                                Contact List
                            </Alert.Heading>
                            <Table>
                                <thead>
                                    <tr>
                                        <th>Profile</th>
                                        <th>Name</th>
                                        <th>Email</th>
                                        <th>Phone number</th>
                                    </tr>
                                </thead>
                                <tbody></tbody>
                            </Table>
                        </main>
                    </>
                )}
            </AuthConsumer>{' '}
        </section>
    )
}

export default Home
